/**
 * Filters system for Atlas Games website
 */

class Filters {
    constructor(containerSelector, onFilterChange) {
        this.container = document.querySelector(containerSelector);
        this.onFilterChange = onFilterChange;
        this.activeFilters = {};
        this.filterOptions = {};
    }

    /**
     * Initialize filters with options
     */
    init(filterOptions) {
        this.filterOptions = filterOptions;
        this.createFilterUI();
        this.bindEvents();
        this.loadFromURL();
    }

    /**
     * Create filter UI elements
     */
    createFilterUI() {
        if (!this.container) return;

        const filtersHTML = `
            <div class="search-filters">
                <div class="search-bar">
                    <input 
                        type="text" 
                        id="search-input" 
                        class="search-input" 
                        placeholder="Search..." 
                        aria-label="Search"
                    >
                    <button type="button" class="btn btn-primary" id="search-btn">Search</button>
                </div>

                <div class="filters-row">
                    ${this.createFilterSelect('status', 'Status', this.filterOptions.statuses)}
                    ${this.createFilterSelect('genre', 'Genre', this.filterOptions.genres)}
                    ${this.createFilterSelect('platform', 'Platform', this.filterOptions.platforms)}
                    ${this.createFilterSelect('role', 'Role', this.filterOptions.roles)}
                    ${this.createFilterSelect('competition', 'Competition', this.filterOptions.competitions)}
                    ${this.createSortSelect()}
                    <div class="filter-group">
                        <button type="button" class="btn btn-secondary" id="clear-filters">Clear All</button>
                    </div>
                </div>

                ${this.createTagsFilter()}
            </div>
        `;

        this.container.innerHTML = filtersHTML;
    }

    /**
     * Create a filter select dropdown
     */
    createFilterSelect(key, label, options) {
        if (!options || options.length === 0) return '';

        return `
            <div class="filter-group">
                <label for="filter-${key}">${label}</label>
                <select id="filter-${key}" class="filter-select" data-filter="${key}">
                    <option value="all">All ${label}s</option>
                    ${options.map(option => 
                        `<option value="${option}">${option}</option>`
                    ).join('')}
                </select>
            </div>
        `;
    }

    /**
     * Create sort select dropdown
     */
    createSortSelect() {
        return `
            <div class="filter-group">
                <label for="sort-select">Sort By</label>
                <select id="sort-select" class="filter-select">
                    <option value="date-desc">Date (Newest)</option>
                    <option value="date-asc">Date (Oldest)</option>
                    <option value="title-asc">Title (A-Z)</option>
                    <option value="title-desc">Title (Z-A)</option>
                    <option value="status-asc">Status</option>
                </select>
            </div>
        `;
    }

    /**
     * Create tags filter checkboxes
     */
    createTagsFilter() {
        if (!this.filterOptions.tags || this.filterOptions.tags.length === 0) {
            return '';
        }

        return `
            <div class="tags-filter">
                <h4>Filter by Tags:</h4>
                <div class="tags-grid">
                    ${this.filterOptions.tags.map(tag => `
                        <label class="tag-checkbox">
                            <input type="checkbox" value="${tag}" data-filter="tags">
                            <span class="tag">${tag}</span>
                        </label>
                    `).join('')}
                </div>
            </div>
        `;
    }

    /**
     * Bind event listeners
     */
    bindEvents() {
        const searchInput = document.getElementById('search-input');
        const searchBtn = document.getElementById('search-btn');
        const clearBtn = document.getElementById('clear-filters');
        const sortSelect = document.getElementById('sort-select');

        // Search functionality
        if (searchInput) {
            const debouncedSearch = Utils.debounce(() => {
                this.handleFilterChange();
            }, 300);

            searchInput.addEventListener('input', debouncedSearch);
            searchInput.addEventListener('keypress', (e) => {
                if (e.key === 'Enter') {
                    this.handleFilterChange();
                }
            });
        }

        if (searchBtn) {
            searchBtn.addEventListener('click', () => this.handleFilterChange());
        }

        // Filter selects
        document.querySelectorAll('.filter-select').forEach(select => {
            select.addEventListener('change', () => this.handleFilterChange());
        });

        // Tag checkboxes
        document.querySelectorAll('input[data-filter="tags"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => this.handleFilterChange());
        });

        // Clear filters
        if (clearBtn) {
            clearBtn.addEventListener('click', () => this.clearAllFilters());
        }
    }

    /**
     * Handle filter changes
     */
    handleFilterChange() {
        this.activeFilters = this.getActiveFilters();

        // Update URL
        Utils.updateURLParams(this.activeFilters);

        // Trigger callback
        if (this.onFilterChange) {
            this.onFilterChange(this.activeFilters);
        }
    }

    /**
     * Get currently active filters
     */
    getActiveFilters() {
        const filters = {};

        // Search query
        const searchInput = document.getElementById('search-input');
        if (searchInput && searchInput.value.trim()) {
            filters.query = searchInput.value.trim();
        }

        // Filter selects
        document.querySelectorAll('.filter-select').forEach(select => {
            const filterType = select.dataset.filter;
            if (filterType && select.value !== 'all') {
                filters[filterType] = select.value;
            }
        });

        // Sort
        const sortSelect = document.getElementById('sort-select');
        if (sortSelect) {
            const [sortBy, sortOrder] = sortSelect.value.split('-');
            filters.sortBy = sortBy;
            filters.sortOrder = sortOrder;
        }

        // Tags
        const selectedTags = [];
        document.querySelectorAll('input[data-filter="tags"]:checked').forEach(checkbox => {
            selectedTags.push(checkbox.value);
        });
        if (selectedTags.length > 0) {
            filters.tags = selectedTags;
        }

        return filters;
    }

    /**
     * Load filters from URL parameters
     */
    loadFromURL() {
        const params = Utils.getURLParams();

        Object.keys(params).forEach(key => {
            if (key === 'query') {
                const searchInput = document.getElementById('search-input');
                if (searchInput) {
                    searchInput.value = params[key];
                }
            } else if (key === 'tags') {
                const tags = Array.isArray(params[key]) ? params[key] : [params[key]];
                tags.forEach(tag => {
                    const checkbox = document.querySelector(`input[data-filter="tags"][value="${tag}"]`);
                    if (checkbox) {
                        checkbox.checked = true;
                    }
                });
            } else if (key === 'sortBy' || key === 'sortOrder') {
                // Handle sort separately
                const sortBy = params.sortBy || 'date';
                const sortOrder = params.sortOrder || 'desc';
                const sortSelect = document.getElementById('sort-select');
                if (sortSelect) {
                    sortSelect.value = `${sortBy}-${sortOrder}`;
                }
            } else {
                const select = document.querySelector(`[data-filter="${key}"]`);
                if (select) {
                    select.value = params[key];
                }
            }
        });

        // Trigger initial filter if URL has parameters
        if (Object.keys(params).length > 0) {
            this.handleFilterChange();
        }
    }

    /**
     * Clear all active filters
     */
    clearAllFilters() {
        // Clear search input
        const searchInput = document.getElementById('search-input');
        if (searchInput) {
            searchInput.value = '';
        }

        // Reset all selects
        document.querySelectorAll('.filter-select').forEach(select => {
            if (select.id === 'sort-select') {
                select.value = 'date-desc';
            } else {
                select.value = 'all';
            }
        });

        // Uncheck all tag checkboxes
        document.querySelectorAll('input[data-filter="tags"]').forEach(checkbox => {
            checkbox.checked = false;
        });

        // Clear URL parameters
        Utils.updateURLParams({});

        // Trigger filter change
        this.handleFilterChange();
    }

    /**
     * Update filter options (useful for dynamic updates)
     */
    updateOptions(newOptions) {
        this.filterOptions = newOptions;
        this.createFilterUI();
        this.bindEvents();
    }

    /**
     * Get active filter count for display
     */
    getActiveFilterCount() {
        const filters = this.getActiveFilters();
        let count = 0;

        if (filters.query) count++;
        if (filters.status && filters.status !== 'all') count++;
        if (filters.genre && filters.genre !== 'all') count++;
        if (filters.platform && filters.platform !== 'all') count++;
        if (filters.role && filters.role !== 'all') count++;
        if (filters.competition && filters.competition !== 'all') count++;
        if (filters.tags && filters.tags.length > 0) count += filters.tags.length;

        return count;
    }
}

// Export for use in other modules
window.Filters = Filters;

// Injects header, nav, and footer into the page
function injectIncludes() {
  // Helper to fetch and inject HTML
  function inject(selector, url, cb) {
    fetch(url).then(r => r.text()).then(html => {
      document.querySelector(selector).outerHTML = html;
      if (cb) cb();
    });
  }

  // Header (contains nav placeholder)
  inject('header#site-header, #site-header', '/reusables/header.html', function() {
    // After header injected, inject nav into nav placeholder
    fetch('/reusables/navigationbar.html').then(r => r.text()).then(navHtml => {
      var nav = document.getElementById('main-nav-include');
      if (nav) nav.outerHTML = navHtml;
      // Highlight active link
      var links = document.querySelectorAll('#main-nav a');
      var path = location.pathname.replace(/\/index\.html$/, '/');
      links.forEach(function(link) {
        if (link.getAttribute('href') === path || (path === '/' && link.getAttribute('href') === '/index.html')) {
          link.classList.add('active');
        }
      });
    });
  });

  // Footer
  inject('footer#site-footer, #site-footer', '/reusables/footer.html');
}

document.addEventListener('DOMContentLoaded', injectIncludes);

/**
 * Includes system for Atlas Games website
 * Loads reusable components (header, nav, footer) into pages
 */

class IncludesLoader {
    constructor() {
        this.loadIncludes();
    }

    async loadIncludes() {
        try {
            // Load header
            await this.loadComponent('header', 'reusables/header.html');

            // Load navigation
            await this.loadComponent('nav', 'reusables/navigationbar.html');

            // Load footer
            await this.loadComponent('footer', 'reusables/footer.html');

            // Initialize navigation after loading
            this.initializeNavigation();

        } catch (error) {
            console.error('Error loading includes:', error);
        }
    }

    async loadComponent(elementId, filePath) {
        const element = document.getElementById(elementId);
        if (!element) return;

        try {
            const response = await fetch(filePath);
            if (!response.ok) {
                throw new Error(`Failed to load ${filePath}: ${response.status}`);
            }

            const content = await response.text();
            element.innerHTML = content;

        } catch (error) {
            console.error(`Error loading ${filePath}:`, error);
            // Fallback content
            if (elementId === 'nav') {
                element.innerHTML = '<nav><a href="index.html">Atlas Games</a></nav>';
            }
        }
    }

    initializeNavigation() {
        // Mobile menu toggle
        const navToggle = document.querySelector('.nav-toggle');
        const navMenu = document.querySelector('.nav-menu');

        if (navToggle && navMenu) {
            navToggle.addEventListener('click', () => {
                const isExpanded = navToggle.getAttribute('aria-expanded') === 'true';
                navToggle.setAttribute('aria-expanded', !isExpanded);
                navMenu.classList.toggle('active');
                document.body.classList.toggle('nav-open');
            });
        }

        // Set active nav item based on current page
        this.setActiveNavItem();

        // Close mobile menu when clicking outside
        document.addEventListener('click', (e) => {
            if (!e.target.closest('.main-nav') && navMenu?.classList.contains('active')) {
                navMenu.classList.remove('active');
                navToggle?.setAttribute('aria-expanded', 'false');
                document.body.classList.remove('nav-open');
            }
        });
    }

    setActiveNavItem() {
        const currentPage = window.location.pathname.split('/').pop() || 'index.html';
        const navLinks = document.querySelectorAll('.nav-menu a');

        navLinks.forEach(link => {
            const href = link.getAttribute('href');
            if (href === currentPage || (currentPage === '' && href === 'index.html')) {
                link.classList.add('active');
                link.setAttribute('aria-current', 'page');
            }
        });
    }
}

// Initialize when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
    new IncludesLoader();
});

// Export for use in other modules
window.IncludesLoader = IncludesLoader;

// ListingLoader: Loads and renders listings for games, blogs, team, awards
(function() {
  const pageMap = {
    'games.html': {
      data: '/data/games.json',
      card: renderGameCard,
      filterKey: 'genre',
      sortKeys: ['title', 'releaseDate']
    },
    'blogs.html': {
      data: '/data/blogs.json',
      card: renderBlogCard,
      filterKey: null,
      sortKeys: ['date', 'title']
    },
    'team.html': {
      data: '/data/team.json',
      card: renderTeamCard,
      filterKey: 'role',
      sortKeys: ['name', 'role']
    },
    'awards.html': {
      data: '/data/awards.json',
      card: renderAwardCard,
      filterKey: 'category',
      sortKeys: ['year', 'title']
    }
  };

  function getPageConf() {
    const name = location.pathname.split('/').pop();
    return pageMap[name];
  }

  window.ListingLoader = {
    load: function() {
      const conf = getPageConf();
      if (!conf) return;
      fetch(conf.data)
        .then(r => r.text())
        .then(txt => JSON.parse(txt.replace(/\/\/.*$/gm, '')))
        .then(data => {
          window.listingData = data;
          window.renderListing(data, conf);
        });
    }
  };

  // Card renderers
  function renderGameCard(game) {
    return `<div class="listing-card">
      <img src="${game.coverImage}" alt="${game.title} cover" loading="lazy">
      <div class="card-title">${game.title}</div>
      <div class="card-meta">${game.genre.join(', ')} | ${game.platforms.join(', ')}</div>
      <div class="card-desc">${game.description}</div>
      <div class="card-tags">${game.genre.map(g=>`<span class='card-tag'>${g}</span>`).join('')}</div>
      <a class="card-link" href="${game.detailPage}">View Details</a>
    </div>`;
  }
  function renderBlogCard(blog) {
    return `<div class="listing-card">
      <img src="${blog.coverImage}" alt="${blog.title} cover" loading="lazy">
      <div class="card-title">${blog.title}</div>
      <div class="card-meta">By ${blog.author} &mdash; ${blog.date}</div>
      <div class="card-desc">${blog.summary}</div>
      <a class="card-link" href="${blog.detailPage}">Read Blog</a>
    </div>`;
  }
  function renderTeamCard(member) {
    return `<div class="listing-card">
      <img src="${member.photo}" alt="${member.name} photo" loading="lazy">
      <div class="card-title">${member.name}</div>
      <div class="card-meta">${member.role}</div>
      <div class="card-desc">${member.bio}</div>
      <div class="card-tags">${member.role ? `<span class='card-tag'>${member.role}</span>` : ''}</div>
      <a class="card-link" href="${member.detailPage}">View Profile</a>
    </div>`;
  }
  function renderAwardCard(award) {
    return `<div class="listing-card">
      <img src="${award.image}" alt="${award.title} image" loading="lazy">
      <div class="card-title">${award.title}</div>
      <div class="card-meta">${award.category} &mdash; ${award.year}</div>
      <div class="card-desc">${award.description}</div>
      <div class="card-tags">${award.category ? `<span class='card-tag'>${award.category}</span>` : ''}</div>
      <a class="card-link" href="${award.detailPage}">Award Details</a>
    </div>`;
  }

  // Expose card renderers for search/filters
  window.renderListing = function(data, conf) {
    const cards = data.map(conf.card).join('');
    document.getElementById('listing-cards').innerHTML = cards;
    document.getElementById('no-results').style.display = data.length ? 'none' : '';
  };

  // Auto-load on DOMContentLoaded
  document.addEventListener('DOMContentLoaded', function() {
    window.ListingLoader.load();
  });
})();

/**
 * Listing loader for Atlas Games website
 * Handles loading and displaying data for games, blogs, team, awards
 */

class ListingLoader {
    constructor(config) {
        this.dataUrl = config.dataUrl;
        this.containerSelector = config.containerSelector;
        this.filtersSelector = config.filtersSelector;
        this.cardTemplate = config.cardTemplate;
        this.searchFields = config.searchFields || ['title'];
        this.itemsPerPage = config.itemsPerPage || 12;

        this.data = [];
        this.filteredData = [];
        this.currentPage = 1;
        this.search = null;
        this.filters = null;

        this.init();
    }

    /**
     * Initialize the listing loader
     */
    async init() {
        try {
            await this.loadData();
            this.initializeSearch();
            this.initializeFilters();
            this.render();
        } catch (error) {
            this.handleError(error);
        }
    }

    /**
     * Load data from JSON file
     */
    async loadData() {
        const container = document.querySelector(this.containerSelector);
        if (container) {
            container.appendChild(Utils.createLoadingSpinner());
        }

        try {
            const response = await fetch(this.dataUrl);
            if (!response.ok) {
                throw new Error(`Failed to load data: ${response.status}`);
            }

            this.data = await response.json();
            this.filteredData = [...this.data];

        } catch (error) {
            console.error('Error loading data:', error);
            throw error;
        }
    }

    /**
     * Initialize search functionality
     */
    initializeSearch() {
        this.search = new Search(this.data, this.searchFields);
    }

    /**
     * Initialize filters
     */
    initializeFilters() {
        const filterOptions = this.search.getFilterOptions();

        this.filters = new Filters(this.filtersSelector, (activeFilters) => {
            this.applyFilters(activeFilters);
        });

        this.filters.init(filterOptions);
    }

    /**
     * Apply filters and update display
     */
    applyFilters(filters) {
        // Get filtered and sorted data
        this.filteredData = this.search.advancedSearch(filters);

        if (filters.sortBy) {
            this.filteredData = this.search.sort(
                this.filteredData,
                filters.sortBy,
                filters.sortOrder || 'desc'
            );
        }

        // Reset to first page
        this.currentPage = 1;

        // Update display
        this.render();
        this.updateResultsCount();
    }

    /**
     * Render the current page
     */
    render() {
        const container = document.querySelector(this.containerSelector);
        if (!container) return;

        // Clear container
        container.innerHTML = '';

        // Show loading state
        if (this.filteredData.length === 0 && this.data.length > 0) {
            container.appendChild(Utils.createNoResultsMessage());
            return;
        }

        // Calculate pagination
        const startIndex = (this.currentPage - 1) * this.itemsPerPage;
        const endIndex = startIndex + this.itemsPerPage;
        const pageData = this.filteredData.slice(startIndex, endIndex);

        // Render items
        const grid = document.createElement('div');
        grid.className = 'grid grid-3';

        pageData.forEach(item => {
            const card = this.createCard(item);
            grid.appendChild(card);
        });

        container.appendChild(grid);

        // Add pagination if needed
        if (this.filteredData.length > this.itemsPerPage) {
            container.appendChild(this.createPagination());
        }

        // Initialize lazy loading for images
        Utils.initLazyLoading();
    }

    /**
     * Create a card element based on the template
     */
    createCard(item) {
        const card = document.createElement('article');
        card.className = 'card';

        // Use template function if provided, otherwise use default
        if (this.cardTemplate && typeof this.cardTemplate === 'function') {
            card.innerHTML = this.cardTemplate(item);
        } else {
            card.innerHTML = this.getDefaultCardTemplate(item);
        }

        return card;
    }

    /**
     * Default card template
     */
    getDefaultCardTemplate(item) {
        const date = item.date || item.releaseDate || '';
        const formattedDate = date ? Utils.formatDate(date) : '';

        const tags = item.tags ? item.tags.map(tag => 
            `<span class="tag">${tag}</span>`
        ).join('') : '';

        const status = item.status ? `<span class="status status-${item.status.toLowerCase().replace(/\s+/g, '-')}">${item.status}</span>` : '';

        return `
            <img src="${item.thumbnail}" alt="${item.title}" class="card-image" loading="lazy">
            <div class="card-content">
                <h3 class="card-title">
                    <a href="${item.detailPath}">${item.title}</a>
                </h3>
                ${formattedDate ? `<div class="card-meta">${formattedDate}</div>` : ''}
                ${item.role ? `<div class="card-meta">${item.role}</div>` : ''}
                ${item.competition ? `<div class="card-meta">${item.competition}</div>` : ''}
                ${item.rank ? `<div class="card-meta">Rank: ${item.rank}</div>` : ''}
                <p class="card-description">${item.description || item.excerpt || item.bio || ''}</p>
                ${tags ? `<div class="card-tags">${tags}</div>` : ''}
                ${status}
            </div>
        `;
    }

    /**
     * Create pagination controls
     */
    createPagination() {
        const totalPages = Math.ceil(this.filteredData.length / this.itemsPerPage);

        const pagination = document.createElement('nav');
        pagination.className = 'pagination';
        pagination.setAttribute('aria-label', 'Pagination navigation');

        const paginationList = document.createElement('ul');
        paginationList.className = 'pagination-list';

        // Previous button
        if (this.currentPage > 1) {
            const prevButton = this.createPaginationButton(
                this.currentPage - 1,
                'Previous',
                'prev'
            );
            paginationList.appendChild(prevButton);
        }

        // Page numbers
        const startPage = Math.max(1, this.currentPage - 2);
        const endPage = Math.min(totalPages, this.currentPage + 2);

        for (let i = startPage; i <= endPage; i++) {
            const pageButton = this.createPaginationButton(i, i.toString());
            if (i === this.currentPage) {
                pageButton.classList.add('active');
                pageButton.setAttribute('aria-current', 'page');
            }
            paginationList.appendChild(pageButton);
        }

        // Next button
        if (this.currentPage < totalPages) {
            const nextButton = this.createPaginationButton(
                this.currentPage + 1,
                'Next',
                'next'
            );
            paginationList.appendChild(nextButton);
        }

        pagination.appendChild(paginationList);
        return pagination;
    }

    /**
     * Create pagination button
     */
    createPaginationButton(page, text, type = 'page') {
        const li = document.createElement('li');
        const button = document.createElement('button');

        button.textContent = text;
        button.className = `pagination-btn ${type}`;
        button.addEventListener('click', () => this.goToPage(page));

        li.appendChild(button);
        return li;
    }

    /**
     * Navigate to specific page
     */
    goToPage(page) {
        this.currentPage = page;
        this.render();

        // Scroll to top of results
        const container = document.querySelector(this.containerSelector);
        if (container) {
            Utils.scrollToElement(container, 100);
        }
    }

    /**
     * Update results count display
     */
    updateResultsCount() {
        const countElement = document.getElementById('results-count');
        if (countElement) {
            const total = this.filteredData.length;
            const showing = Math.min(this.itemsPerPage, total);
            const start = (this.currentPage - 1) * this.itemsPerPage + 1;
            const end = Math.min(start + showing - 1, total);

            if (total === 0) {
                countElement.textContent = 'No results found';
            } else {
                countElement.textContent = `Showing ${start}-${end} of ${total} results`;
            }
        }
    }

    /**
     * Handle errors
     */
    handleError(error) {
        const container = document.querySelector(this.containerSelector);
        if (container) {
            container.innerHTML = '';
            container.appendChild(Utils.handleError(error, 'Failed to load data. Please try again later.'));
        }
    }

    /**
     * Refresh data
     */
    async refresh() {
        try {
            await this.loadData();
            this.search.updateData(this.data);
            this.filteredData = [...this.data];
            this.currentPage = 1;
            this.render();
        } catch (error) {
            this.handleError(error);
        }
    }

    /**
     * Get current data
     */
    getData() {
        return this.data;
    }

    /**
     * Get filtered data
     */
    getFilteredData() {
        return this.filteredData;
    }
}

// Export for use in other modules
window.ListingLoader = ListingLoader;

// Search, filter, sort, and update listing
(function() {
  let debounceTimer;
  let lastQuery = '';

  function getConf() {
    const name = location.pathname.split('/').pop();
    return window.ListingLoader && window.ListingLoader.load && window.renderListing && window.listingData && window.ListingLoader ?
      (function() {
        const conf = {
          'games.html': {filterKey: 'genre', searchKeys: ['title','genre'], sortKeys: ['title','releaseDate']},
          'blogs.html': {filterKey: null, searchKeys: ['title','summary'], sortKeys: ['date','title']},
          'team.html': {filterKey: 'role', searchKeys: ['name','role'], sortKeys: ['name','role']},
          'awards.html': {filterKey: 'category', searchKeys: ['title','category'], sortKeys: ['year','title']}
        };
        return conf[name];
      })() : null;
  }

  function getFilters(data, key) {
    if (!key) return [];
    const vals = new Set();
    data.forEach(item => {
      if (Array.isArray(item[key])) item[key].forEach(v=>vals.add(v));
      else if (item[key]) vals.add(item[key]);
    });
    return Array.from(vals).sort();
  }

  function updateURL(params) {
    const url = new URL(location);
    Object.keys(params).forEach(k => {
      if (params[k]) url.searchParams.set(k, params[k]);
      else url.searchParams.delete(k);
    });
    history.replaceState(null, '', url);
  }

  function applySearchFilterSort() {
    const conf = getConf();
    if (!conf) return;
    let data = window.listingData || [];
    const q = (document.getElementById('search-input')?.value || '').trim().toLowerCase();
    const filter = document.querySelector('.filter-chip.selected')?.dataset.value || '';
    const sort = document.getElementById('sort-dropdown')?.value || conf.sortKeys[0];
    // Search
    if (q) {
      data = data.filter(item => conf.searchKeys.some(k => {
        if (Array.isArray(item[k])) return item[k].join(' ').toLowerCase().includes(q);
        return (item[k]||'').toLowerCase().includes(q);
      }));
    }
    // Filter
    if (conf.filterKey && filter) {
      data = data.filter(item => {
        if (Array.isArray(item[conf.filterKey])) return item[conf.filterKey].includes(filter);
        return item[conf.filterKey] === filter;
      });
    }
    // Sort
    data = data.slice().sort((a,b) => {
      if (sort === 'releaseDate' || sort === 'date' || sort === 'year') {
        return (b[sort]||'').localeCompare(a[sort]||'');
      }
      return (a[sort]||'').localeCompare(b[sort]||'');
    });
    window.renderListing(data, window.ListingLoader.getPageConf());
    updateURL({q, filter, sort});
  }

  function debounceApply() {
    clearTimeout(debounceTimer);
    debounceTimer = setTimeout(applySearchFilterSort, 200);
  }

  document.addEventListener('DOMContentLoaded', function() {
    if (!window.listingData) return;
    const conf = getConf();
    // Search
    document.getElementById('search-input').addEventListener('input', debounceApply);
    // Filter chips
    const chips = document.getElementById('filter-chips');
    if (conf.filterKey && chips) {
      const filters = getFilters(window.listingData, conf.filterKey);
      chips.innerHTML = filters.map(f => `<span class="filter-chip" data-value="${f}">${f}</span>`).join('');
      chips.addEventListener('click', e => {
        if (e.target.classList.contains('filter-chip')) {
          chips.querySelectorAll('.filter-chip').forEach(c => c.classList.remove('selected'));
          e.target.classList.add('selected');
          applySearchFilterSort();
        }
      });
    }
    // Sort
    document.getElementById('sort-dropdown').addEventListener('change', applySearchFilterSort);
    // Initial state from URL
    const url = new URL(location);
    if (url.searchParams.get('q')) document.getElementById('search-input').value = url.searchParams.get('q');
    if (url.searchParams.get('filter')) {
      const chip = chips?.querySelector(`[data-value="${url.searchParams.get('filter')}"]`);
      if (chip) chip.classList.add('selected');
    }
    if (url.searchParams.get('sort')) document.getElementById('sort-dropdown').value = url.searchParams.get('sort');
    applySearchFilterSort();
  });
})();

/**
 * Search functionality for Atlas Games website
 */

class Search {
    constructor(data, searchFields = ['title']) {
        this.data = data;
        this.searchFields = searchFields;
        this.originalData = [...data];
    }

    /**
     * Perform search across specified fields
     */
    search(query) {
        if (!query || query.trim() === '') {
            return this.originalData;
        }

        const searchTerm = query.toLowerCase().trim();

        return this.originalData.filter(item => {
            return this.searchFields.some(field => {
                const value = item[field];

                if (Array.isArray(value)) {
                    return value.some(v => 
                        v.toString().toLowerCase().includes(searchTerm)
                    );
                }

                return value && value.toString().toLowerCase().includes(searchTerm);
            });
        });
    }

    /**
     * Advanced search with multiple criteria
     */
    advancedSearch(criteria) {
        let results = this.originalData;

        // Text search
        if (criteria.query) {
            results = this.search(criteria.query);
        }

        // Filter by tags
        if (criteria.tags && criteria.tags.length > 0) {
            results = results.filter(item => {
                if (!item.tags) return false;
                return criteria.tags.every(tag => item.tags.includes(tag));
            });
        }

        // Filter by status
        if (criteria.status && criteria.status !== 'all') {
            results = results.filter(item => item.status === criteria.status);
        }

        // Filter by genre
        if (criteria.genre && criteria.genre !== 'all') {
            results = results.filter(item => item.genre === criteria.genre);
        }

        // Filter by platform
        if (criteria.platform && criteria.platform !== 'all') {
            results = results.filter(item => {
                if (!item.platform) return false;
                return item.platform.includes(criteria.platform);
            });
        }

        // Filter by role (for team members)
        if (criteria.role && criteria.role !== 'all') {
            results = results.filter(item => item.role === criteria.role);
        }

        // Filter by competition (for awards)
        if (criteria.competition && criteria.competition !== 'all') {
            results = results.filter(item => item.competition === criteria.competition);
        }

        // Date range filter
        if (criteria.dateFrom || criteria.dateTo) {
            results = results.filter(item => {
                if (!item.date && !item.releaseDate) return false;

                const itemDate = new Date(item.date || item.releaseDate);

                if (criteria.dateFrom) {
                    const fromDate = new Date(criteria.dateFrom);
                    if (itemDate < fromDate) return false;
                }

                if (criteria.dateTo) {
                    const toDate = new Date(criteria.dateTo);
                    if (itemDate > toDate) return false;
                }

                return true;
            });
        }

        return results;
    }

    /**
     * Sort results by specified field and order
     */
    sort(data, sortBy, sortOrder = 'asc') {
        const sortedData = [...data];

        sortedData.sort((a, b) => {
            let valueA = a[sortBy];
            let valueB = b[sortBy];

            // Handle date sorting
            if (sortBy === 'date' || sortBy === 'releaseDate') {
                valueA = new Date(valueA);
                valueB = new Date(valueB);
            }

            // Handle string sorting
            if (typeof valueA === 'string') {
                valueA = valueA.toLowerCase();
                valueB = valueB.toLowerCase();
            }

            // Handle array sorting (for tags, platforms, etc.)
            if (Array.isArray(valueA)) {
                valueA = valueA.join(',').toLowerCase();
                valueB = valueB.join(',').toLowerCase();
            }

            let comparison = 0;
            if (valueA > valueB) {
                comparison = 1;
            } else if (valueA < valueB) {
                comparison = -1;
            }

            return sortOrder === 'desc' ? comparison * -1 : comparison;
        });

        return sortedData;
    }

    /**
     * Get filter options from data
     */
    getFilterOptions() {
        const options = {
            tags: Utils.getUniqueValues(this.originalData, 'tags'),
            genres: Utils.getUniqueValues(this.originalData, 'genre'),
            platforms: Utils.getUniqueValues(this.originalData, 'platform'),
            statuses: Utils.getUniqueValues(this.originalData, 'status'),
            roles: Utils.getUniqueValues(this.originalData, 'role'),
            competitions: Utils.getUniqueValues(this.originalData, 'competition')
        };

        // Remove empty arrays
        Object.keys(options).forEach(key => {
            if (options[key].length === 0) {
                delete options[key];
            }
        });

        return options;
    }

    /**
     * Highlight search terms in text
     */
    highlightSearchTerms(text, searchTerm) {
        if (!searchTerm || !text) return text;

        const regex = new RegExp(`(${searchTerm})`, 'gi');
        return text.replace(regex, '<mark>$1</mark>');
    }

    /**
     * Get search suggestions based on partial input
     */
    getSuggestions(partialQuery, limit = 5) {
        if (!partialQuery || partialQuery.length < 2) {
            return [];
        }

        const suggestions = new Set();
        const query = partialQuery.toLowerCase();

        this.originalData.forEach(item => {
            this.searchFields.forEach(field => {
                const value = item[field];

                if (Array.isArray(value)) {
                    value.forEach(v => {
                        if (v.toString().toLowerCase().includes(query)) {
                            suggestions.add(v);
                        }
                    });
                } else if (value && value.toString().toLowerCase().includes(query)) {
                    // Add words that start with the query
                    const words = value.toString().split(' ');
                    words.forEach(word => {
                        if (word.toLowerCase().startsWith(query)) {
                            suggestions.add(word);
                        }
                    });
                }
            });
        });

        return Array.from(suggestions).slice(0, limit);
    }

    /**
     * Update data (useful for dynamic data updates)
     */
    updateData(newData) {
        this.data = newData;
        this.originalData = [...newData];
    }
}

// Export for use in other modules
window.Search = Search;

/**
 * Utility functions for Atlas Games website
 */

class Utils {
    /**
     * Debounce function to limit rapid function calls
     */
    static debounce(func, wait) {
        let timeout;
        return function executedFunction(...args) {
            const later = () => {
                clearTimeout(timeout);
                func(...args);
            };
            clearTimeout(timeout);
            timeout = setTimeout(later, wait);
        };
    }

    /**
     * Format date for display
     */
    static formatDate(dateString) {
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    }

    /**
     * Calculate reading time based on word count
     */
    static calculateReadTime(text, wordsPerMinute = 200) {
        const wordCount = text.split(/\s+/).length;
        return Math.ceil(wordCount / wordsPerMinute);
    }

    /**
     * Sanitize HTML to prevent XSS
     */
    static sanitizeHTML(str) {
        const temp = document.createElement('div');
        temp.textContent = str;
        return temp.innerHTML;
    }

    /**
     * Generate slug from title
     */
    static generateSlug(title) {
        return title
            .toLowerCase()
            .replace(/[^\w\s-]/g, '')
            .replace(/[\s_-]+/g, '-')
            .replace(/^-+|-+$/g, '');
    }

    /**
     * Update URL query parameters without page reload
     */
    static updateURLParams(params) {
        const url = new URL(window.location);
        Object.keys(params).forEach(key => {
            if (params[key]) {
                url.searchParams.set(key, params[key]);
            } else {
                url.searchParams.delete(key);
            }
        });
        window.history.replaceState(null, '', url);
    }

    /**
     * Get URL query parameters
     */
    static getURLParams() {
        const params = {};
        const urlParams = new URLSearchParams(window.location.search);
        for (const [key, value] of urlParams) {
            params[key] = value;
        }
        return params;
    }

    /**
     * Lazy load images when they enter viewport
     */
    static initLazyLoading() {
        const imageObserver = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const img = entry.target;
                    img.src = img.dataset.src;
                    img.classList.remove('lazy');
                    imageObserver.unobserve(img);
                }
            });
        });

        document.querySelectorAll('img[data-src]').forEach(img => {
            imageObserver.observe(img);
        });
    }

    /**
     * Create loading spinner element
     */
    static createLoadingSpinner() {
        const spinner = document.createElement('div');
        spinner.className = 'loading';
        spinner.innerHTML = 'Loading...';
        return spinner;
    }

    /**
     * Create no results message element
     */
    static createNoResultsMessage(message = 'No results found') {
        const noResults = document.createElement('div');
        noResults.className = 'no-results';
        noResults.innerHTML = `<p>${message}</p>`;
        return noResults;
    }

    /**
     * Smooth scroll to element
     */
    static scrollToElement(element, offset = 0) {
        const elementPosition = element.offsetTop - offset;
        window.scrollTo({
            top: elementPosition,
            behavior: 'smooth'
        });
    }

    /**
     * Handle errors gracefully
     */
    static handleError(error, userMessage = 'Something went wrong') {
        console.error('Error:', error);

        // Show user-friendly error message
        const errorDiv = document.createElement('div');
        errorDiv.className = 'error-message';
        errorDiv.innerHTML = `<p>${userMessage}</p>`;

        return errorDiv;
    }

    /**
     * Validate email format
     */
    static isValidEmail(email) {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    }

    /**
     * Get unique values from array of objects by property
     */
    static getUniqueValues(array, property) {
        const values = array.flatMap(item => {
            const value = item[property];
            return Array.isArray(value) ? value : [value];
        });
        return [...new Set(values)].filter(Boolean).sort();
    }

    /**
     * Truncate text to specified length
     */
    static truncateText(text, maxLength, suffix = '...') {
        if (text.length <= maxLength) return text;
        return text.substring(0, maxLength - suffix.length) + suffix;
    }

    /**
     * Create breadcrumb navigation
     */
    static createBreadcrumbs(path) {
        const breadcrumbs = document.createElement('nav');
        breadcrumbs.className = 'breadcrumbs';
        breadcrumbs.setAttribute('aria-label', 'Breadcrumb navigation');

        const parts = path.split('/').filter(Boolean);
        let currentPath = '';

        const breadcrumbList = document.createElement('ol');

        // Home link
        const homeItem = document.createElement('li');
        homeItem.innerHTML = '<a href="index.html">Home</a>';
        breadcrumbList.appendChild(homeItem);

        parts.forEach((part, index) => {
            currentPath += '/' + part;
            const item = document.createElement('li');

            if (index === parts.length - 1) {
                item.textContent = part.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
                item.setAttribute('aria-current', 'page');
            } else {
                const link = document.createElement('a');
                link.href = currentPath + '.html';
                link.textContent = part.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
                item.appendChild(link);
            }

            breadcrumbList.appendChild(item);
        });

        breadcrumbs.appendChild(breadcrumbList);
        return breadcrumbs;
    }
}

// Export for use in other modules
window.Utils = Utils;

