//   "author": string,
//   "date": string (YYYY-MM-DD),
//   "summary": string,
//   "coverImage": string, // repo-relative path
//   "detailPage": string // repo-relative path
// }
[
  {
    "id": "blog-001",
    "title": "Behind the Scenes of Skyward Quest",
    "author": "Jane Devlin",
    "date": "2024-07-01",
    "summary": "A look at the making of our flagship game.",
    "coverImage": "assets/images/blogs/skyward-dev.jpg",
    "detailPage": "blog-collection/skyward-quest-dev.html"
  }
]

/* ==========================================================================
   ATLAS GAMES - GAME RENDERING
   ========================================================================== */

/**
 * Game rendering functionality for games.html and game.html pages
 */
class GameRenderer {
  constructor() {
    this.games = [];
    this.filteredGames = [];
    this.currentFilters = {
      genre: 'all',
      platform: 'all',
      status: 'all',
      search: '',
      sort: 'newest'
    };
    this.init();
  }

  /**
   * Initialize game renderer
   */
  async init() {
    try {
      this.games = await AtlasUtils.fetchJSON('data/games.json');
      this.filteredGames = [...this.games];

      // Set up page based on current location
      if (window.location.pathname.includes('games.html')) {
        this.setupGamesPage();
      } else if (window.location.pathname.includes('game.html')) {
        this.setupGameDetailPage();
      }

    } catch (error) {
      console.error('Error loading games:', error);
      AtlasUtils.showError('Failed to load games. Please refresh the page.');
    }
  }

  /**
   * Setup games listing page
   */
  setupGamesPage() {
    this.setupFilters();
    this.setupSearch();
    this.renderGames();
  }

  /**
   * Setup game detail page
   */
  setupGameDetailPage() {
    const gameId = AtlasRouter.getParam('id') || AtlasRouter.getParam('slug');
    if (gameId) {
      const game = this.games.find(g => g.id === gameId || g.slug === gameId);
      if (game) {
        this.renderGameDetail(game);
      }
    }
  }

  /**
   * Setup filter controls
   */
  setupFilters() {
    // Get unique values for filter options
    const genres = [...new Set(this.games.flatMap(game => game.genre))].sort();
    const platforms = [...new Set(this.games.flatMap(game => game.platforms))].sort();
    const statuses = [...new Set(this.games.map(game => game.releaseStatus))].sort();

    // Render filter options
    this.renderFilterOptions('genre-filter', genres, 'All Genres');
    this.renderFilterOptions('platform-filter', platforms, 'All Platforms');
    this.renderFilterOptions('status-filter', statuses, 'All Status');

    // Setup filter event listeners
    this.setupFilterListeners();
  }

  /**
   * Render filter options
   */
  renderFilterOptions(containerId, options, defaultText) {
    const container = document.getElementById(containerId);
    if (!container) return;

    const filterHtml = `
      <div class="filter-options">
        <button class="filter-option is-active" data-value="all">${defaultText}</button>
        ${options.map(option => 
          `<button class="filter-option" data-value="${option.toLowerCase()}">${option}</button>`
        ).join('')}
      </div>
    `;

    container.innerHTML = filterHtml;
  }

  /**
   * Setup filter event listeners
   */
  setupFilterListeners() {
    // Genre filter
    const genreFilter = document.getElementById('genre-filter');
    if (genreFilter) {
      genreFilter.addEventListener('click', (e) => {
        if (e.target.classList.contains('filter-option')) {
          this.updateFilter('genre', e.target.dataset.value, genreFilter);
        }
      });
    }

    // Platform filter
    const platformFilter = document.getElementById('platform-filter');
    if (platformFilter) {
      platformFilter.addEventListener('click', (e) => {
        if (e.target.classList.contains('filter-option')) {
          this.updateFilter('platform', e.target.dataset.value, platformFilter);
        }
      });
    }

    // Status filter
    const statusFilter = document.getElementById('status-filter');
    if (statusFilter) {
      statusFilter.addEventListener('click', (e) => {
        if (e.target.classList.contains('filter-option')) {
          this.updateFilter('status', e.target.dataset.value, statusFilter);
        }
      });
    }

    // Sort dropdown
    const sortSelect = document.getElementById('sort-select');
    if (sortSelect) {
      sortSelect.addEventListener('change', (e) => {
        this.currentFilters.sort = e.target.value;
        this.applyFilters();
      });
    }
  }

  /**
   * Setup search functionality
   */
  setupSearch() {
    const searchInput = document.getElementById('game-search');
    if (searchInput) {
      const debouncedSearch = AtlasUtils.debounce((value) => {
        this.currentFilters.search = value;
        this.applyFilters();
      }, 300);

      searchInput.addEventListener('input', (e) => {
        debouncedSearch(e.target.value);
      });
    }
  }

  /**
   * Update filter and UI state
   */
  updateFilter(filterType, value, container) {
    // Update filter state
    this.currentFilters[filterType] = value;

    // Update UI
    container.querySelectorAll('.filter-option').forEach(btn => {
      btn.classList.remove('is-active');
    });
    container.querySelector(`[data-value="${value}"]`).classList.add('is-active');

    // Apply filters
    this.applyFilters();
  }

  /**
   * Apply all filters
   */
  applyFilters() {
    this.filteredGames = this.games.filter(game => {
      // Genre filter
      if (this.currentFilters.genre !== 'all') {
        if (!game.genre.some(g => g.toLowerCase() === this.currentFilters.genre)) {
          return false;
        }
      }

      // Platform filter
      if (this.currentFilters.platform !== 'all') {
        if (!game.platforms.some(p => p.toLowerCase() === this.currentFilters.platform)) {
          return false;
        }
      }

      // Status filter
      if (this.currentFilters.status !== 'all') {
        if (game.releaseStatus.toLowerCase() !== this.currentFilters.status) {
          return false;
        }
      }

      // Search filter
      if (this.currentFilters.search) {
        const searchTerm = this.currentFilters.search.toLowerCase();
        const searchableText = `${game.title} ${game.shortDescription} ${game.genre.join(' ')}`.toLowerCase();
        if (!searchableText.includes(searchTerm)) {
          return false;
        }
      }

      return true;
    });

    // Apply sorting
    this.sortGames();

    // Re-render games
    this.renderGames();

    // Update URL
    AtlasRouter.applyFilters(this.currentFilters);
  }

  /**
   * Sort games based on current sort option
   */
  sortGames() {
    switch (this.currentFilters.sort) {
      case 'newest':
        this.filteredGames.sort((a, b) => new Date(b.releaseDate || '1970-01-01') - new Date(a.releaseDate || '1970-01-01'));
        break;
      case 'oldest':
        this.filteredGames.sort((a, b) => new Date(a.releaseDate || '1970-01-01') - new Date(b.releaseDate || '1970-01-01'));
        break;
      case 'alphabetical':
        this.filteredGames.sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'reverse-alphabetical':
        this.filteredGames.sort((a, b) => b.title.localeCompare(a.title));
        break;
    }
  }

  /**
   * Render games grid
   */
  renderGames() {
    const container = document.getElementById('games-grid');
    if (!container) return;

    if (this.filteredGames.length === 0) {
      container.innerHTML = `
        <div class="col-span-full text-center py-5xl">
          <h3 class="text-2xl font-semibold mb-md">No games found</h3>
          <p class="text-muted">Try adjusting your filters or search terms.</p>
          <button class="btn btn-primary mt-lg" onclick="gameRenderer.clearFilters()">Clear Filters</button>
        </div>
      `;
      return;
    }

    const gamesHtml = this.filteredGames.map(game => this.renderGameCard(game)).join('');
    container.innerHTML = gamesHtml;

    // Update results count
    const resultsCount = document.getElementById('results-count');
    if (resultsCount) {
      resultsCount.textContent = `${this.filteredGames.length} game${this.filteredGames.length !== 1 ? 's' : ''} found`;
    }
  }

  /**
   * Render individual game card
   */
  renderGameCard(game) {
    const statusBadgeClass = this.getStatusBadgeClass(game.releaseStatus);
    const gameUrl = `game.html?id=${game.id}`;

    return `
      <div class="game-card card">
        <div class="card-image">
          <img src="${game.thumbnail}" alt="${game.title}" loading="lazy">
          <div class="absolute top-2 right-2">
            <span class="badge ${statusBadgeClass}">${game.releaseStatus}</span>
          </div>
        </div>
        <div class="card-content">
          <div class="card-header">
            <h3 class="card-title">
              <a href="${gameUrl}" class="text-primary">${game.title}</a>
            </h3>
            <div class="tags">
              ${game.genre.map(g => `<span class="tag">${g}</span>`).join('')}
            </div>
          </div>
          <div class="card-body">
            <p class="text-muted">${AtlasUtils.truncate(game.shortDescription, 120)}</p>
            <div class="mt-md">
              <strong>Platforms:</strong> ${game.platforms.join(', ')}
            </div>
          </div>
          <div class="card-footer">
            <div class="flex gap-sm">
              <a href="${gameUrl}" class="btn btn-primary btn-sm flex-1">View Details</a>
              ${game.itchUrl ? `<a href="${game.itchUrl}" target="_blank" rel="noopener" class="btn btn-outline btn-sm">Play</a>` : ''}
            </div>
          </div>
        </div>
      </div>
    `;
  }

  /**
   * Get badge class for release status
   */
  getStatusBadgeClass(status) {
    switch (status.toLowerCase()) {
      case 'released': return 'badge-success';
      case 'beta': return 'badge-warning';
      case 'in development': return 'badge-info';
      default: return 'badge-secondary';
    }
  }

  /**
   * Clear all filters
   */
  clearFilters() {
    this.currentFilters = {
      genre: 'all',
      platform: 'all',
      status: 'all',
      search: '',
      sort: 'newest'
    };

    // Reset UI
    document.querySelectorAll('.filter-option').forEach(btn => {
      btn.classList.remove('is-active');
    });
    document.querySelectorAll('.filter-option[data-value="all"]').forEach(btn => {
      btn.classList.add('is-active');
    });

    const searchInput = document.getElementById('game-search');
    if (searchInput) {
      searchInput.value = '';
    }

    const sortSelect = document.getElementById('sort-select');
    if (sortSelect) {
      sortSelect.value = 'newest';
    }

    this.applyFilters();
  }

  /**
   * Render game detail page
   */
  renderGameDetail(game) {
    window.renderGameDetail = (gameData) => {
      const main = document.querySelector('main');
      if (!main) return;

      main.innerHTML = `
        <div class="container">
          <!-- Hero Section -->
          <div class="hero">
            <div class="hero-content">
              <div class="flex flex-wrap gap-sm justify-center mb-lg">
                ${gameData.genre.map(g => `<span class="tag">${g}</span>`).join('')}
                <span class="badge ${this.getStatusBadgeClass(gameData.releaseStatus)}">${gameData.releaseStatus}</span>
              </div>
              <h1 class="hero-title">${gameData.title}</h1>
              <p class="hero-subtitle">${gameData.shortDescription}</p>
              <div class="hero-actions">
                ${gameData.itchUrl ? `<a href="${gameData.itchUrl}" target="_blank" rel="noopener" class="btn btn-primary btn-lg">Play Now</a>` : ''}
                ${gameData.trailer ? `<a href="${gameData.trailer}" target="_blank" rel="noopener" class="btn btn-outline btn-lg">Watch Trailer</a>` : ''}
                <a href="games.html" class="btn btn-ghost btn-lg">← Back to Games</a>
              </div>
            </div>
          </div>

          <!-- Screenshots Gallery -->
          ${gameData.screenshots && gameData.screenshots.length > 0 ? `
          <section class="section">
            <h2 class="text-3xl font-bold mb-xl text-center">Screenshots</h2>
            <div class="gallery">
              ${gameData.screenshots.map(screenshot => `
                <div class="gallery-item" tabindex="0" role="button" aria-label="View screenshot">
                  <img src="${screenshot}" alt="${gameData.title} screenshot" loading="lazy">
                </div>
              `).join('')}
            </div>
          </section>
          ` : ''}

          <!-- Game Details -->
          <section class="section">
            <div class="grid md:grid-cols-2 gap-xl">
              <div>
                <h2 class="text-3xl font-bold mb-lg">About ${gameData.title}</h2>
                <div class="prose">
                  <p>${gameData.longDescription}</p>
                </div>

                <div class="mt-xl">
                  <h3 class="text-xl font-semibold mb-md">Game Information</h3>
                  <dl class="space-y-sm">
                    <div class="flex">
                      <dt class="font-medium w-32">Platforms:</dt>
                      <dd>${gameData.platforms.join(', ')}</dd>
                    </div>
                    <div class="flex">
                      <dt class="font-medium w-32">Status:</dt>
                      <dd><span class="badge ${this.getStatusBadgeClass(gameData.releaseStatus)}">${gameData.releaseStatus}</span></dd>
                    </div>
                    ${gameData.releaseDate ? `
                    <div class="flex">
                      <dt class="font-medium w-32">Release Date:</dt>
                      <dd>${AtlasUtils.formatDate(gameData.releaseDate)}</dd>
                    </div>
                    ` : ''}
                    <div class="flex">
                      <dt class="font-medium w-32">Genres:</dt>
                      <dd>${gameData.genre.join(', ')}</dd>
                    </div>
                  </dl>
                </div>
              </div>

              ${gameData.features && gameData.features.length > 0 ? `
              <div>
                <h3 class="text-xl font-semibold mb-lg">Key Features</h3>
                <ul class="space-y-sm">
                  ${gameData.features.map(feature => `<li class="flex items-start gap-sm">
                    <svg class="w-5 h-5 text-primary mt-1 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                      <path fill-rule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clip-rule="evenodd"></path>
                    </svg>
                    <span>${feature}</span>
                  </li>`).join('')}
                </ul>
              </div>
              ` : ''}
            </div>
          </section>

          <!-- Related Games -->
          <section class="section">
            <h2 class="text-3xl font-bold mb-xl text-center">More Games</h2>
            <div class="grid md:grid-cols-3 gap-lg">
              ${this.getRelatedGames(gameData, 3).map(relatedGame => this.renderGameCard(relatedGame)).join('')}
            </div>
          </section>
        </div>
      `;

      // Add JSON-LD structured data
      this.addGameStructuredData(gameData);
    };

    // Call the render function
    window.renderGameDetail(game);
  }

  /**
   * Get related games (same genre, excluding current game)
   */
  getRelatedGames(currentGame, limit = 3) {
    const related = this.games
      .filter(game => 
        game.id !== currentGame.id && 
        game.genre.some(g => currentGame.genre.includes(g))
      )
      .slice(0, limit);

    // If not enough related games, fill with random games
    if (related.length < limit) {
      const remaining = this.games
        .filter(game => game.id !== currentGame.id && !related.includes(game))
        .slice(0, limit - related.length);
      related.push(...remaining);
    }

    return related;
  }

  /**
   * Add JSON-LD structured data for games
   */
  addGameStructuredData(game) {
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "VideoGame",
      "name": game.title,
      "description": game.shortDescription,
      "genre": game.genre,
      "gamePlatform": game.platforms,
      "publisher": {
        "@type": "Organization",
        "name": "Atlas Games"
      },
      "datePublished": game.releaseDate,
      "applicationCategory": "Game"
    };

    if (game.screenshots && game.screenshots.length > 0) {
      structuredData.screenshot = game.screenshots[0];
    }

    // Remove existing game structured data
    const existingScript = document.querySelector('script[type="application/ld+json"][data-game]');
    if (existingScript) {
      existingScript.remove();
    }

    // Add new structured data
    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.setAttribute('data-game', '');
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);
  }

  /**
   * Update filters from URL parameters (called by router)
   */
  updateFilters(filters) {
    this.currentFilters = { ...this.currentFilters, ...filters };

    // Update UI to match filters
    Object.entries(filters).forEach(([key, value]) => {
      if (value && value !== 'all') {
        const filterElement = document.querySelector(`[data-value="${value}"]`);
        if (filterElement) {
          // Remove active class from siblings
          filterElement.parentNode.querySelectorAll('.filter-option').forEach(btn => {
            btn.classList.remove('is-active');
          });
          // Add active class to current
          filterElement.classList.add('is-active');
        }
      }
    });

    // Update search input
    if (filters.search) {
      const searchInput = document.getElementById('game-search');
      if (searchInput) {
        searchInput.value = filters.search;
      }
    }

    // Update sort select
    if (filters.sort) {
      const sortSelect = document.getElementById('sort-select');
      if (sortSelect) {
        sortSelect.value = filters.sort;
      }
    }

    this.applyFilters();
  }
}

// Initialize game renderer when DOM is ready
document.addEventListener('atlas:ready', () => {
  window.gameRenderer = new GameRenderer();
});

// Make renderer available globally for router
window.updateGamesFilters = (filters) => {
  if (window.gameRenderer) {
    window.gameRenderer.updateFilters(filters);
  }
};

/* ==========================================================================
   ATLAS GAMES - BLOG/POSTS RENDERING
   ========================================================================== */

/**
 * Blog rendering functionality for blog.html and post.html pages
 */
class BlogRenderer {
  constructor() {
    this.posts = [];
    this.filteredPosts = [];
    this.currentFilters = {
      tag: 'all',
      search: '',
      page: 1
    };
    this.postsPerPage = 6;
    this.init();
  }

  /**
   * Initialize blog renderer
   */
  async init() {
    try {
      this.posts = await AtlasUtils.fetchJSON('data/posts.json');
      // Sort posts by date (newest first)
      this.posts.sort((a, b) => new Date(b.date) - new Date(a.date));
      this.filteredPosts = [...this.posts];

      // Set up page based on current location
      if (window.location.pathname.includes('blog.html')) {
        this.setupBlogPage();
      } else if (window.location.pathname.includes('post.html')) {
        this.setupPostDetailPage();
      }

    } catch (error) {
      console.error('Error loading posts:', error);
      AtlasUtils.showError('Failed to load blog posts. Please refresh the page.');
    }
  }

  /**
   * Setup blog listing page
   */
  setupBlogPage() {
    this.setupFilters();
    this.setupSearch();
    this.renderPosts();
  }

  /**
   * Setup post detail page
   */
  setupPostDetailPage() {
    const postId = AtlasRouter.getParam('id') || AtlasRouter.getParam('slug');
    if (postId) {
      const post = this.posts.find(p => p.id === postId || p.slug === postId);
      if (post) {
        this.renderPostDetail(post);
      }
    }
  }

  /**
   * Setup filter controls
   */
  setupFilters() {
    // Get unique tags from all posts
    const allTags = [...new Set(this.posts.flatMap(post => post.tags))].sort();

    // Render tag filter options
    this.renderTagFilter(allTags);

    // Setup filter event listeners
    this.setupFilterListeners();
  }

  /**
   * Render tag filter options
   */
  renderTagFilter(tags) {
    const container = document.getElementById('tag-filter');
    if (!container) return;

    const filterHtml = `
      <div class="filter-options">
        <button class="filter-option is-active" data-value="all">All Topics</button>
        ${tags.map(tag => 
          `<button class="filter-option" data-value="${tag.toLowerCase()}">${tag}</button>`
        ).join('')}
      </div>
    `;

    container.innerHTML = filterHtml;
  }

  /**
   * Setup filter event listeners
   */
  setupFilterListeners() {
    // Tag filter
    const tagFilter = document.getElementById('tag-filter');
    if (tagFilter) {
      tagFilter.addEventListener('click', (e) => {
        if (e.target.classList.contains('filter-option')) {
          this.updateFilter('tag', e.target.dataset.value, tagFilter);
        }
      });
    }
  }

  /**
   * Setup search functionality
   */
  setupSearch() {
    const searchInput = document.getElementById('blog-search');
    if (searchInput) {
      const debouncedSearch = AtlasUtils.debounce((value) => {
        this.currentFilters.search = value;
        this.currentFilters.page = 1; // Reset to first page on search
        this.applyFilters();
      }, 300);

      searchInput.addEventListener('input', (e) => {
        debouncedSearch(e.target.value);
      });
    }
  }

  /**
   * Update filter and UI state
   */
  updateFilter(filterType, value, container) {
    // Update filter state
    this.currentFilters[filterType] = value;
    this.currentFilters.page = 1; // Reset to first page on filter change

    // Update UI
    container.querySelectorAll('.filter-option').forEach(btn => {
      btn.classList.remove('is-active');
    });
    container.querySelector(`[data-value="${value}"]`).classList.add('is-active');

    // Apply filters
    this.applyFilters();
  }

  /**
   * Apply all filters
   */
  applyFilters() {
    this.filteredPosts = this.posts.filter(post => {
      // Tag filter
      if (this.currentFilters.tag !== 'all') {
        if (!post.tags.some(tag => tag.toLowerCase() === this.currentFilters.tag)) {
          return false;
        }
      }

      // Search filter
      if (this.currentFilters.search) {
        const searchTerm = this.currentFilters.search.toLowerCase();
        const searchableText = `${post.title} ${post.excerpt} ${post.content} ${post.tags.join(' ')}`.toLowerCase();
        if (!searchableText.includes(searchTerm)) {
          return false;
        }
      }

      return true;
    });

    // Re-render posts with pagination
    this.renderPosts();

    // Update URL
    AtlasRouter.applyFilters(this.currentFilters);
  }

  /**
   * Render posts with pagination
   */
  renderPosts() {
    const container = document.getElementById('posts-grid');
    if (!container) return;

    if (this.filteredPosts.length === 0) {
      container.innerHTML = `
        <div class="col-span-full text-center py-5xl">
          <h3 class="text-2xl font-semibold mb-md">No posts found</h3>
          <p class="text-muted">Try adjusting your filters or search terms.</p>
          <button class="btn btn-primary mt-lg" onclick="blogRenderer.clearFilters()">Clear Filters</button>
        </div>
      `;
      this.renderPagination(0, 0);
      return;
    }

    // Calculate pagination
    const totalPages = Math.ceil(this.filteredPosts.length / this.postsPerPage);
    const startIndex = (this.currentFilters.page - 1) * this.postsPerPage;
    const endIndex = startIndex + this.postsPerPage;
    const paginatedPosts = this.filteredPosts.slice(startIndex, endIndex);

    // Render posts
    const postsHtml = paginatedPosts.map(post => this.renderPostCard(post)).join('');
    container.innerHTML = postsHtml;

    // Render pagination
    this.renderPagination(this.currentFilters.page, totalPages);

    // Update results count
    const resultsCount = document.getElementById('results-count');
    if (resultsCount) {
      resultsCount.textContent = `${this.filteredPosts.length} post${this.filteredPosts.length !== 1 ? 's' : ''} found`;
    }
  }

  /**
   * Render individual post card
   */
  renderPostCard(post) {
    const postUrl = `post.html?id=${post.id}`;
    const readTime = this.calculateReadTime(post.content);

    return `
      <article class="card">
        <div class="card-image">
          <img src="${post.coverImage}" alt="${post.title}" loading="lazy">
        </div>
        <div class="card-content">
          <div class="card-header">
            <h2 class="card-title">
              <a href="${postUrl}" class="text-primary">${post.title}</a>
            </h2>
            <div class="flex items-center gap-md text-sm text-muted mb-sm">
              <span>By ${post.author}</span>
              <span>•</span>
              <time datetime="${post.date}">${AtlasUtils.formatDate(post.date)}</time>
              <span>•</span>
              <span>${readTime} min read</span>
            </div>
          </div>
          <div class="card-body">
            <p class="text-muted mb-md">${post.excerpt}</p>
            <div class="tags">
              ${post.tags.map(tag => `<a href="blog.html?tag=${tag.toLowerCase()}" class="tag">${tag}</a>`).join('')}
            </div>
          </div>
          <div class="card-footer">
            <a href="${postUrl}" class="btn btn-primary btn-sm">Read More</a>
          </div>
        </div>
      </article>
    `;
  }

  /**
   * Calculate estimated read time
   */
  calculateReadTime(content) {
    const wordsPerMinute = 200;
    const wordCount = content.replace(/<[^>]*>/g, '').split(/\s+/).length;
    return Math.max(1, Math.round(wordCount / wordsPerMinute));
  }

  /**
   * Render pagination
   */
  renderPagination(currentPage, totalPages) {
    const container = document.getElementById('pagination');
    if (!container || totalPages <= 1) {
      if (container) container.innerHTML = '';
      return;
    }

    let paginationHtml = '<div class="pagination">';

    // Previous button
    if (currentPage > 1) {
      paginationHtml += `<button class="pagination-item" onclick="blogRenderer.goToPage(${currentPage - 1})" aria-label="Previous page">‹</button>`;
    } else {
      paginationHtml += `<button class="pagination-item" disabled aria-label="Previous page">‹</button>`;
    }

    // Page numbers
    const startPage = Math.max(1, currentPage - 2);
    const endPage = Math.min(totalPages, currentPage + 2);

    if (startPage > 1) {
      paginationHtml += `<button class="pagination-item" onclick="blogRenderer.goToPage(1)">1</button>`;
      if (startPage > 2) {
        paginationHtml += `<span class="pagination-item" disabled>…</span>`;
      }
    }

    for (let i = startPage; i <= endPage; i++) {
      const isActive = i === currentPage ? 'is-active' : '';
      paginationHtml += `<button class="pagination-item ${isActive}" onclick="blogRenderer.goToPage(${i})">${i}</button>`;
    }

    if (endPage < totalPages) {
      if (endPage < totalPages - 1) {
        paginationHtml += `<span class="pagination-item" disabled>…</span>`;
      }
      paginationHtml += `<button class="pagination-item" onclick="blogRenderer.goToPage(${totalPages})">${totalPages}</button>`;
    }

    // Next button
    if (currentPage < totalPages) {
      paginationHtml += `<button class="pagination-item" onclick="blogRenderer.goToPage(${currentPage + 1})" aria-label="Next page">›</button>`;
    } else {
      paginationHtml += `<button class="pagination-item" disabled aria-label="Next page">›</button>`;
    }

    paginationHtml += '</div>';
    container.innerHTML = paginationHtml;
  }

  /**
   * Go to specific page
   */
  goToPage(page) {
    this.currentFilters.page = page;
    this.renderPosts();

    // Scroll to top of posts
    const postsGrid = document.getElementById('posts-grid');
    if (postsGrid) {
      postsGrid.scrollIntoView({ behavior: 'smooth' });
    }
  }

  /**
   * Clear all filters
   */
  clearFilters() {
    this.currentFilters = {
      tag: 'all',
      search: '',
      page: 1
    };

    // Reset UI
    document.querySelectorAll('.filter-option').forEach(btn => {
      btn.classList.remove('is-active');
    });
    document.querySelector('.filter-option[data-value="all"]')?.classList.add('is-active');

    const searchInput = document.getElementById('blog-search');
    if (searchInput) {
      searchInput.value = '';
    }

    this.applyFilters();
  }

  /**
   * Render post detail page
   */
  renderPostDetail(post) {
    window.renderPostDetail = (postData, allPosts) => {
      const main = document.querySelector('main');
      if (!main) return;

      // Get previous and next posts
      const currentIndex = allPosts.findIndex(p => p.id === postData.id);
      const prevPost = currentIndex > 0 ? allPosts[currentIndex - 1] : null;
      const nextPost = currentIndex < allPosts.length - 1 ? allPosts[currentIndex + 1] : null;

      const readTime = this.calculateReadTime(postData.content);

      main.innerHTML = `
        <div class="container max-w-4xl">
          <article class="py-xl">
            <!-- Back to blog -->
            <div class="mb-lg">
              <a href="blog.html" class="btn btn-ghost">← Back to Blog</a>
            </div>

            <!-- Post header -->
            <header class="mb-xl text-center">
              <div class="tags justify-center mb-lg">
                ${postData.tags.map(tag => `<a href="blog.html?tag=${tag.toLowerCase()}" class="tag">${tag}</a>`).join('')}
              </div>
              <h1 class="text-4xl md:text-5xl font-bold mb-lg">${postData.title}</h1>
              <div class="flex items-center justify-center gap-md text-muted mb-lg">
                <span>By <strong>${postData.author}</strong></span>
                <span>•</span>
                <time datetime="${postData.date}">${AtlasUtils.formatDate(postData.date)}</time>
                <span>•</span>
                <span>${readTime} minute read</span>
              </div>
              ${postData.coverImage ? `
              <div class="aspect-video rounded-lg overflow-hidden mb-lg">
                <img src="${postData.coverImage}" alt="${postData.title}" class="img-cover">
              </div>
              ` : ''}
            </header>

            <!-- Post content -->
            <div class="prose max-w-none">
              ${postData.content}
            </div>

            <!-- Post footer -->
            <footer class="mt-xl pt-xl border-t">
              <div class="text-center mb-xl">
                <h3 class="text-xl font-semibold mb-md">Share this post</h3>
                <div class="flex gap-md justify-center">
                  <a href="https://twitter.com/intent/tweet?text=${encodeURIComponent(postData.title)}&url=${encodeURIComponent(window.location.href)}" 
                     target="_blank" rel="noopener" class="btn btn-outline btn-sm">
                    Share on Twitter
                  </a>
                  <a href="https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(window.location.href)}" 
                     target="_blank" rel="noopener" class="btn btn-outline btn-sm">
                    Share on Facebook
                  </a>
                </div>
              </div>

              <!-- Navigation -->
              <div class="grid md:grid-cols-2 gap-lg">
                ${prevPost ? `
                <div class="card">
                  <div class="card-content">
                    <p class="text-sm text-muted mb-sm">Previous Post</p>
                    <h4 class="font-semibold">
                      <a href="post.html?id=${prevPost.id}" class="text-primary">${prevPost.title}</a>
                    </h4>
                  </div>
                </div>
                ` : '<div></div>'}

                ${nextPost ? `
                <div class="card">
                  <div class="card-content text-right">
                    <p class="text-sm text-muted mb-sm">Next Post</p>
                    <h4 class="font-semibold">
                      <a href="post.html?id=${nextPost.id}" class="text-primary">${nextPost.title}</a>
                    </h4>
                  </div>
                </div>
                ` : '<div></div>'}
              </div>
            </footer>
          </article>

          <!-- Related posts -->
          <section class="py-xl border-t">
            <h2 class="text-3xl font-bold mb-xl text-center">Related Posts</h2>
            <div class="grid md:grid-cols-2 lg:grid-cols-3 gap-lg">
              ${this.getRelatedPosts(postData, 3).map(relatedPost => this.renderPostCard(relatedPost)).join('')}
            </div>
          </section>
        </div>
      `;

      // Add structured data
      this.addPostStructuredData(postData);
    };

    // Call the render function
    window.renderPostDetail(post, this.posts);
  }

  /**
   * Get related posts (same tags, excluding current post)
   */
  getRelatedPosts(currentPost, limit = 3) {
    const related = this.posts
      .filter(post => 
        post.id !== currentPost.id && 
        post.tags.some(tag => currentPost.tags.includes(tag))
      )
      .slice(0, limit);

    // If not enough related posts, fill with recent posts
    if (related.length < limit) {
      const remaining = this.posts
        .filter(post => post.id !== currentPost.id && !related.includes(post))
        .slice(0, limit - related.length);
      related.push(...remaining);
    }

    return related;
  }

  /**
   * Add JSON-LD structured data for blog posts
   */
  addPostStructuredData(post) {
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "BlogPosting",
      "headline": post.title,
      "description": post.excerpt,
      "datePublished": post.date,
      "author": {
        "@type": "Person",
        "name": post.author
      },
      "publisher": {
        "@type": "Organization",
        "name": "Atlas Games"
      },
      "mainEntityOfPage": {
        "@type": "WebPage",
        "@id": window.location.href
      }
    };

    if (post.coverImage) {
      structuredData.image = post.coverImage;
    }

    // Remove existing post structured data
    const existingScript = document.querySelector('script[type="application/ld+json"][data-post]');
    if (existingScript) {
      existingScript.remove();
    }

    // Add new structured data
    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.setAttribute('data-post', '');
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);
  }

  /**
   * Update filters from URL parameters (called by router)
   */
  updateFilters(filters) {
    this.currentFilters = { ...this.currentFilters, ...filters };

    // Update UI to match filters
    if (filters.tag && filters.tag !== 'all') {
      const tagElement = document.querySelector(`[data-value="${filters.tag}"]`);
      if (tagElement) {
        tagElement.parentNode.querySelectorAll('.filter-option').forEach(btn => {
          btn.classList.remove('is-active');
        });
        tagElement.classList.add('is-active');
      }
    }

    // Update search input
    if (filters.search) {
      const searchInput = document.getElementById('blog-search');
      if (searchInput) {
        searchInput.value = filters.search;
      }
    }

    this.applyFilters();
  }
}

// Initialize blog renderer when DOM is ready
document.addEventListener('atlas:ready', () => {
  if (window.location.pathname.includes('blog.html') || window.location.pathname.includes('post.html')) {
    window.blogRenderer = new BlogRenderer();
  }
});

// Make renderer available globally for router
window.updateBlogFilters = (filters) => {
  if (window.blogRenderer) {
    window.blogRenderer.updateFilters(filters);
  }
};

/* ==========================================================================
   ATLAS GAMES - TEAM RENDERING
   ========================================================================== */

/**
 * Team rendering functionality for team.html page
 */
class TeamRenderer {
  constructor() {
    this.team = [];
    this.init();
  }

  /**
   * Initialize team renderer
   */
  async init() {
    try {
      this.team = await AtlasUtils.fetchJSON('data/team.json');
      this.renderTeam();
    } catch (error) {
      console.error('Error loading team:', error);
      AtlasUtils.showError('Failed to load team information. Please refresh the page.');
    }
  }

  /**
   * Render team members
   */
  renderTeam() {
    const container = document.getElementById('team-grid');
    if (!container) return;

    const teamHtml = this.team.map(member => this.renderTeamCard(member)).join('');
    container.innerHTML = teamHtml;

    // Setup member detail modals
    this.setupMemberModals();
  }

  /**
   * Render individual team member card
   */
  renderTeamCard(member) {
    return `
      <div class="team-card card" data-member="${member.id}">
        <div class="card-image">
          <img src="${member.photo}" alt="${member.name}" loading="lazy">
        </div>
        <div class="card-content">
          <div class="card-header">
            <h3 class="card-title">${member.name}</h3>
            <p class="card-subtitle">${member.role}</p>
          </div>
          <div class="card-body">
            <p class="text-muted mb-md">${member.shortBio}</p>
            <div class="tags">
              ${member.skills.slice(0, 3).map(skill => `<span class="tag">${skill}</span>`).join('')}
              ${member.skills.length > 3 ? `<span class="tag">+${member.skills.length - 3} more</span>` : ''}
            </div>
          </div>
          <div class="card-footer">
            <button class="btn btn-primary btn-sm btn-block" data-modal="member-modal-${member.id}">
              Learn More
            </button>
            <div class="social-links mt-md justify-center">
              ${this.renderSocialLinks(member.links)}
            </div>
          </div>
        </div>
      </div>
    `;
  }

  /**
   * Render social links for team member
   */
  renderSocialLinks(links) {
    if (!links) return '';

    const linkHtml = [];

    if (links.twitter) {
      linkHtml.push(`<a href="${links.twitter}" target="_blank" rel="noopener" aria-label="Twitter">
        <svg width="20" height="20"><use href="#icon-twitter"></use></svg>
      </a>`);
    }

    if (links.linkedin) {
      linkHtml.push(`<a href="${links.linkedin}" target="_blank" rel="noopener" aria-label="LinkedIn">
        <svg width="20" height="20"><use href="#icon-linkedin"></use></svg>
      </a>`);
    }

    if (links.github) {
      linkHtml.push(`<a href="${links.github}" target="_blank" rel="noopener" aria-label="GitHub">
        <svg width="20" height="20"><use href="#icon-github"></use></svg>
      </a>`);
    }

    if (links.portfolio || links.website) {
      const url = links.portfolio || links.website;
      linkHtml.push(`<a href="${url}" target="_blank" rel="noopener" aria-label="Portfolio">
        <svg width="20" height="20"><use href="#icon-external"></use></svg>
      </a>`);
    }

    if (links.artstation) {
      linkHtml.push(`<a href="${links.artstation}" target="_blank" rel="noopener" aria-label="ArtStation">
        <svg width="20" height="20"><use href="#icon-external"></use></svg>
      </a>`);
    }

    if (links.instagram) {
      linkHtml.push(`<a href="${links.instagram}" target="_blank" rel="noopener" aria-label="Instagram">
        <svg width="20" height="20"><use href="#icon-instagram"></use></svg>
      </a>`);
    }

    return linkHtml.join('');
  }

  /**
   * Setup member detail modals
   */
  setupMemberModals() {
    // Create modals for each team member
    this.team.forEach(member => {
      this.createMemberModal(member);
    });
  }

  /**
   * Create modal for team member
   */
  createMemberModal(member) {
    const modalId = `member-modal-${member.id}`;

    // Check if modal already exists
    if (document.getElementById(modalId)) return;

    const modal = document.createElement('div');
    modal.id = modalId;
    modal.className = 'modal-overlay';
    modal.innerHTML = `
      <div class="modal">
        <div class="modal-header">
          <h2 class="modal-title">${member.name}</h2>
          <button class="modal-close" aria-label="Close modal">
            <svg width="24" height="24"><use href="#icon-close"></use></svg>
          </button>
        </div>
        <div class="modal-body">
          <div class="flex flex-col md:flex-row gap-lg">
            <div class="flex-shrink-0 text-center">
              <img src="${member.photo}" alt="${member.name}" 
                   class="w-32 h-32 rounded-full object-cover mx-auto mb-md">
              <h3 class="font-semibold">${member.role}</h3>
              <p class="text-sm text-muted mb-md">Joined ${AtlasUtils.formatDate(member.joinDate, { year: 'numeric', month: 'long' })}</p>
              <div class="social-links justify-center">
                ${this.renderSocialLinks(member.links)}
              </div>
            </div>
            <div class="flex-1">
              <div class="mb-lg">
                <h4 class="font-semibold mb-sm">About</h4>
                <p class="text-muted">${member.longBio}</p>
              </div>

              <div class="mb-lg">
                <h4 class="font-semibold mb-sm">Skills & Expertise</h4>
                <div class="tags">
                  ${member.skills.map(skill => `<span class="tag">${skill}</span>`).join('')}
                </div>
              </div>

              ${member.favoriteGames && member.favoriteGames.length > 0 ? `
              <div class="mb-lg">
                <h4 class="font-semibold mb-sm">Favorite Atlas Games</h4>
                <ul class="text-sm text-muted">
                  ${member.favoriteGames.map(game => `<li>${game}</li>`).join('')}
                </ul>
              </div>
              ` : ''}

              ${member.quote ? `
              <div class="bg-surface p-md rounded-lg border-l-4 border-primary">
                <p class="italic text-muted">"${member.quote}"</p>
              </div>
              ` : ''}
            </div>
          </div>
        </div>
      </div>
    `;

    document.body.appendChild(modal);

    // Setup modal event listeners
    modal.addEventListener('click', (e) => {
      if (e.target === modal) {
        modal.classList.remove('is-open');
        document.body.style.overflow = '';
      }
    });

    const closeBtn = modal.querySelector('.modal-close');
    closeBtn.addEventListener('click', () => {
      modal.classList.remove('is-open');
      document.body.style.overflow = '';
    });

    // Setup modal trigger
    const trigger = document.querySelector(`[data-modal="${modalId}"]`);
    if (trigger) {
      trigger.addEventListener('click', () => {
        modal.classList.add('is-open');
        document.body.style.overflow = 'hidden';
      });
    }
  }

  /**
   * Get team member by ID
   */
  getMember(id) {
    return this.team.find(member => member.id === id);
  }

  /**
   * Get team members by role
   */
  getMembersByRole(role) {
    return this.team.filter(member => 
      member.role.toLowerCase().includes(role.toLowerCase())
    );
  }

  /**
   * Get founders
   */
  getFounders() {
    return this.team.filter(member => 
      member.role.toLowerCase().includes('founder')
    );
  }
}

// Initialize team renderer when DOM is ready
document.addEventListener('atlas:ready', () => {
  if (window.location.pathname.includes('team.html')) {
    window.teamRenderer = new TeamRenderer();
  }
});

